                new Book() { Id = 2, Name = "Shibumi", Author = "Trevanian ", Price = 12.0, Content = "Nicholaï Hel est l'assassin le plus doué de son époque et l'homme le plus recherché du monde. Son secret réside dans sa détermination à atteindre une forme rare d'excellence personnelle : le shibumi. Après avoir été élevé dans le Japon de l'après-guerre et initié à l'art subtil du go, il est désormais retiré dans sa forteresse du Pays basque. Il se retrouve alors traqué par une organisation internationale de terreur et d'anéantissement - la Mother Company - et doit se préparer à un ultime affrontement.", Genres = new List<Genre>() { Espionnage,Classic } },
                new Book() { Id = 3, Name = "La compagnie - le grand roman de la ciaLa compagnie - le grand roman de la cia", Author = "Robert Littell", Price = 17.6, Content = "Dans ce redoutable thriller politique, Robert Littell restitue un demi-siècle de notre histoire. Entre fiction et réalité, personnages fictifs et figures historiques (Kennedy, Eltsine, mais aussi Ben Laden), il dévoile les mécanismes et les dérapages de l'une des organisations les plus tristement célèbres au monde, la CIA. Un roman d'espionnage magistralement orchestré, qui place Littell aux côtés des maîtres du genre, John le Carré en tête.", Genres = new List<Genre>() { Espionnage, Thriller } },
                new Book() { Id = 4, Name = "Lonesome dove t.1", Author = "Larry Mcmurtry", Price = 29.90, Content = "À Lonesome Dove, Texas, les héros sont fatigués. Augustus McCrae et Woodrow Call ont remisé leurs armes après de longues années passées à combattre les Comanches. En cette année 1880, pourtant, l'aventure va les rattraper lorsqu'ils décident de voler du bétail au Mexique et de le convoyer jusque dans le Montana pour y établir un ranch. Commence alors un immense périple à travers l'Ouest, au cours duquel le convoi affrontera de violentes tempêtes, des bandes de tueurs et d'Indiens rebelles... et laissera de nombreux hommes derrière lui.", Genres = new List<Genre>() { Aventure, Classic } },
                new Book() { Id = 5, Name = "Le centaure de l'arctique", Author = "Yves Gauthier ", Price = 12.9, Content = "Parmi les exploits authentiques, il convient de saluer celui de Gleb Travine, ouvrier soviétique qui, en 1927, seul, sans assistance, accomplit à vélo le tour de l'URSS. Parti du Kamtchatka, il gagne Vladivostok, traverse le Caucase et l'Asie centrale, la Crimée, l'Ukraine, atteint Mourmansk d'où il s'élance dans une stupéfiante épopée arctique, roulant sur la banquise, se nourrissant de ce qu'il chasse ou pêche, s'amputant luimême de ses orteils gelés. Quatre ans plus tard, le Centaure de l'Arctique, « l'homme qui chevauche un renne de fer » - comme le décrivent les autochtones -, a bouclé son périple, et rapporte le journal où il a consigné les péripéties du voyage. Hélas, Staline est au pouvoir, les exploits individuels sont proscrits - et le livre qu'écrira Travine sera mis à l'index.", Genres = new List<Genre>() { Classic, SF, Aventure } },
                new Book() { Id = 6, Name = "Minuit, dernière limite : une aventure de jack reacher", Author = "Lee Child", Price = 29.9, Content = "Rien ne peut arrêter Reacher, fidèle à sa réputation.De passage dans une petite ville du Wisconsin,Jack Reacher découvre une bague de West Point dans la vitrine d'un prêteur sur gage. Plus intrigant encore, cette bague a appartenu à une femme. Pourquoi cette ancienne de West Point s'est - elle séparée d'un bijou si précieux, preuve de quatre années de durs combats en Irak et en Afghanistan ? Ancien de West Point lui-même, Reacher soupçonneun vol, voire pire, et décide de retrouver cette femme et de lui rendre sa bague.Ainsi commence un périple de plus en plus violent et crépusculaire qui le verra errer jusque dans les déserts du Wyoming et régler leur compte à tous ceux qui,bikers, dealers et corrompus divers,n'ont aucune envie de le voir fouiner dans leurs trafics. Au fil de son voyage, Reacher comprend que cette bague raconte surtout l'honneur mais aussi l'horreur de ce qu'a vécu et vit encore cette femme il lui faut sauver coûte que coûte.", Genres = new List<Genre>() { Aventure} },
                new Book() { Id = 7, Name = "Bob morane - les naufragés de la pleine lune", Author = "Henri Vernes", Price = 15.3, Content = "Paris, un jour de pluie. Bob Morane reçoit la visite d'un bien étrange messager. Dans ses valises, un billet d'avion pour New-York et la photographie d'une femme.", Genres = new List<Genre>() { Classic, Aventure } },
                new Book() { Id = 8, Name = "Le livre perdu des sortilèges", Author = "Deborah Harkness ", Price = 38.9, Content = "Découvrez Le Livre perdu des sortilèges, le livre de Deborah Harkness. Diana Bishop a renoncé depuis longtemps à un héritage familial compliqué, pour privilégier ses recherches universitaires, une vie simple et ordinaire. Jusqu'au jour où elle emprunte un manuscrit alchimique : l'Ashmole 782. Elle ignore alors qu'elle vient de réveiller un ancien et terrible secret - un secret convoité par de nombreuses et redoutables créatures. Parmi eux, Matthew Clairmont. Un tueur, lui a-t-on dit. Malgré elle, Diana se retrouve au c?ur de la tourmente.", Genres = new List<Genre>() { Fantastique } },
                new Book() { Id = 9, Name = "La formule de dieu", Author = "José Rodrigues Santos", Price = 16.8, Content = "Le Caire, de nos jours. Le cryptologue portugais Tomás Noronha se voit confier le décryptage d'un manuscrit original. Son nom ? Die Gottesformel, La Formule de Dieu. Son auteur ? Albert Einstein lui-même. L'enjeu ? Le mode d'emploi d'une bombe nucléaire surpuissante. Précipité malgré lui au coeur d'une affaire d'espionnage international, Noronha plonge dans les secrets de l'atome... et dans un mystère bien plus grand encore.", Genres = new List<Genre>() { Thriller} },
                new Book() { Id = 10, Name = "Le temple noir", Author = "Jacques Ravenne", Price = 19.8, Content = "1232. En Terre Sainte, une lutte sans merci oppose le Grand Maître des Templiers et le Légat du pape pour posséder un secret revendiqué par toutes les religions et tous les pouvoirs.2012.À Londres,le Temple Noir se réunit et va changer le cours de l'Histoire.Dans les méandres financiers de la City et dans les coulisses des sociétés secrètes, le seul homme à pouvoir éviter le pire est Antoine Marcas, flic et franc - maçon.", Genres = new List<Genre>() { Classic, Thriller } },
                new Book() { Id = 11, Name = "Codex 632 - le secret de christophe colomb", Author = "José Rodrigues Santos ", Price = 38.6, Content = "La vie de Tomás Noronha bascule lorsqu'on lui demande de déchiffrer les notes d'un professeur d'histoire portugais, retrouvé mort dans sa chambre d'hôtel alors qu'il travaillait sur la découverte des Amériques.Au fil de ses investigations,Tomás va découvrir un code qui pourrait bien changer notre vision de l'Histoire. De Lisbonne à Rio, New York ou Jérusalem, le jeune cryptologue se trouve plongé dans l'histoire fascinante des Grandes Découvertes et se heurte à l'une des énigmes que les historiens nont toujours pas réussi à résoudre : l'identité de Christophe Colomb et, par delà son identité, la véritable histoire de la découverte des Amériques.", Genres = new List<Genre>() { Thriller } },
                new Book() { Id = 12, Name = "Les montagnes hallucinées illustré", Author = "Howard Phillips Lovecraft", Price = 29.9, Content = "Corona Mundi... Toit du Monde... » Toutes sortes de formules fantastiques nous vinrent aux lèvres tandis que nous contemplions, depuis notre point d'observation vertigineux, l'incroyable spectacle.Arkham,1933.Le professeur Dyer, éminent géologue,apprend qu'une expédition scientifique partira bientôt pour l'Antarctique avec pour ambition de suivre les traces de celle qu'il avait lui-même dirigée en 1931. Dans l'espoir de dissuader cette tentative,Dyer décide de faire un récit complet des tragiques événements auxquels il survécut,cette fois sans omettre les passages qu'il avait écartés à son retour, de peur d'être pris pour un fou.Deux ans plus tôt, les navires affrétés par l'université Miskatonic avaient accosté le continent glacé au début de l'été austral,et le contingent de quatre professeurs et seize étudiants s'était mis aussitôt au travail. Les premiers résultats ne s'étaient pas fait attendre et le biologiste de l'expédition, le professeur Lake, était parti de son côté avec plusieurs membres de l'équipe afin de suivre une piste fossilifère prometteuse.Au bout de quelques jours à peine il avait annoncé par radio avoir découvert de stupéfiants spécimens d'une espèce inconnue, extraordinairement ancienne, avant de cesser toute communication après une terrible tempête. Pressentant le pire, Dyer s'était porté à leur secours le jour suivant.Ce qu'il avait découvert sur place dépassait ses craintes les plus folles...", Genres = new List<Genre>() { Classic, Fantastique} },
                new Book() { Id = 13, Name = "L'institut", Author = "Stephen King ", Price = 9.6, Content = "En pleine nuit, à Minneapolis, des intrus pénètrent dans la maison de Luke Ellis, un surdoué de douze ans, tuent ses parents et le kidnappent. Quand le jeune garçon se réveille, à l'Institut, la chambre où il se trouve est semblable à la sienne - si ce n'est l'absence de fenêtre. Dans le couloir, d'autres portes cachent d'autres enfants, dotés comme lui de pouvoirs psychiques. Que font-ils là ? Qu'attend-on d'eux ? Et pourquoi ne cherchent-ils pas à s'enfuir ?", Genres = new List<Genre>() { Fantastique } }
                
              
